package com.nekolr.saber.service;

import java.io.IOException;
import java.io.InputStream;

/**
 * All storage service should implements this interface
 *
 * @author nekolr
 */
public interface StorageService {

    /**
     * Upload file
     *
     * @param in          file inputStream
     * @param filename    origin name
     * @param contentType contentType
     * @param length      file length
     * @return A relative url str generated by storage service
     */
    String upload(InputStream in, String filename, String contentType, long length) throws IOException;

    /**
     * Delete file
     *
     * @param filename
     */
    void delete(String filename);
}
